import React, { useContext, useState } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } =
    useContext(ShopContext);
  const [promoCode, setPromoCode] = useState("");

  const handlePromoCodeSubmit = async () => {
    try {
      const response = await fetch("http://localhost:4000/applypromocode", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("auth-token"),
        },
        body: JSON.stringify({ promoCode }),
      });
      if (response.ok) {
        alert("Promo code applied successfully.");
      } else {
        const data = await response.json();
        alert(data.errors || "Failed to apply promo code.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to apply promo code.");
    }
  };

  const handlePlaceOrder = async () => {
    try {
      const response = await fetch("http://localhost:4000/placeorder", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("auth-token"),
        },
        body: JSON.stringify({ promoCode, cartItems }), // Ensure cartItems are passed here
      });
      if (response.ok) {
        alert("Order placed successfully.");
      } else {
        const data = await response.json();
        alert(data.errors || "Failed to place order.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to place order.");
    }
  };

  return (
    <div className="cartitems">
      {/* Cart items rendering */}
      <div className="cartitems-down">
        <div className="cartitems-total">
          {/* Total cart amount and place order button */}
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>₹{getTotalCartAmount()}</p>
            </div>
            <button onClick={handlePlaceOrder}>Place Order</button>
          </div>
        </div>
        {/* Promo code input */}
        <div className="cartitems-promocode">
          <p>If you have a promo code, Enter it here</p>
          <div className="cartitems-promobox">
            <input
              type="text"
              placeholder="promo code"
              value={promoCode}
              onChange={(e) => setPromoCode(e.target.value)}
            />
            <button onClick={handlePromoCodeSubmit}>Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItems;
