import React, { useContext, useState } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } =
    useContext(ShopContext);
  const [promoCode, setPromoCode] = useState("");
  const [placingOrder, setPlacingOrder] = useState(false);

  const handlePlaceOrder = async () => {
    try {
      setPlacingOrder(true);

      const response = await fetch("http://localhost:4000/placeorder", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("auth-token"),
        },
        body: JSON.stringify({
          promoCode: promoCode,
          cartData: cartItems, // Assuming cartItems contains the cart data
        }),
      });

      if (response.ok) {
        alert("Order placed successfully.");
        // You may want to perform additional actions after placing the order, such as updating the UI or redirecting the user.
      } else {
        const data = await response.json();
        alert(data.errors || "Failed to place order.");
      }
    } catch (error) {
      console.error("Error placing order:", error);
      alert("Failed to place order.");
    } finally {
      setPlacingOrder(false);
    }
  };

  return (
    <div className="cartitems">
      {/* Your existing JSX code */}
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>₹{getTotalCartAmount()}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>₹{getTotalCartAmount()}</h3>
            </div>
          </div>
          <button onClick={handlePlaceOrder} disabled={placingOrder}>
            {placingOrder ? "Placing Order..." : "PLACE ORDER"}
          </button>
        </div>
        <div className="cartitems-promocode">
          {/* Your promo code input and submit button */}
        </div>
      </div>
    </div>
  );
};

export default CartItems;
