import React, { useContext, useEffect, useState } from "react";
import { ShopContext } from "../Context/ShopContext";
import { useParams } from "react-router-dom";
import Breadcrum from "../Components/Breadcrums/Breadcrum";
import ProductDisplay from "../Components/ProductDisplay/ProductDisplay";
import DescriptionBox from "../Components/DescriptionBox/DescriptionBox";
import RelatedProducts from "../Components/RelatedProducts/RelatedProducts";

const Product = () => {
  const { all_product, fetchCartData } = useContext(ShopContext);
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  // Fetch cart data when the component mounts
  useEffect(() => {
    fetchCartData(); // Assuming fetchCartData is a function in your ShopContext to fetch cart data
  }, [fetchCartData]);

  useEffect(() => {
    // Find the product with the matching productId
    const foundProduct = all_product.find((e) => e.id === Number(productId));
    setProduct(foundProduct);
  }, [all_product, productId]);

  if (!product) {
    return <div>Loading...</div>; // Render a loading indicator until the product data is fetched
  }

  return (
    <div>
      <Breadcrum product={product} />
      <ProductDisplay product={product} />
      <DescriptionBox />
      <RelatedProducts />
    </div>
  );
};

export default Product;
