import React, { createContext, useEffect, useState } from "react";
import axios from "axios";

export const ShopContext = createContext(null);

// //initialising the cart with zeroes
// const getDefaultCart = () => {
//   let cart = {};
//   for (let index = 0; index < 300; index++) {
//     cart[index] = 0;
//   }
//   return cart;
// };

const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);

  // //initialising the cart with zeroes
  // const getDefaultCart = () => {
  //   let cart = {};
  //   for (let index = 0; index < 300; index++) {
  //     cart[index] = 0;
  //   }
  //   return cart;
  // };

  const [cartItems, setCartItems] = useState({});

  //fetching data from the database onto the products section
  // useEffect(() => {
  //   fetch("http://localhost:4000/allproducts")
  //     .then((res) => res.json())
  //     .then((data) => setAll_Product(data));

  //   if (localStorage.getItem("auth-token")) {
  //     fetch("http://localhost:3001/getcart", {
  //       method: "POST",
  //       headers: {
  //         Accept: "application/json",
  //         "auth-token": `${localStorage.getItem("auth-token")}`,
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify(),
  //     })
  //       .then((resp) => resp.json())
  //       .then((data) => {
  //         setCartItems(data);
  //       });
  //   }
  // }, []);

  useEffect(() => {
    // Fetch cart data from the backend
    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:3001/getcart", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "auth-token": `${localStorage.getItem("auth-token")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(),
      })
        .then((resp) => resp.json())
        .then((data) => {
          // Ensure data is an array and then set the cartItems state
          if (Array.isArray(data.cart)) {
            setCartItems(data.cart);
            console.log(data.cart);
          } else {
            console.error("Invalid cart data format:", data);
          }
        })
        .catch((error) => {
          console.error("Error fetching cart data:", error);
        });
    }
  }, []);

  //add to cart
  //add to cart
  // const addToCart = () => {
  //   if (localStorage.getItem("auth-token")) {
  //     fetch("http://localhost:4002/addtocart", {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json",
  //         "auth-token": localStorage.getItem("auth-token"),
  //       },
  //       body: JSON.stringify({ itemId: "1" }), // Pass itemId as "1" and itemSize as "M"
  //     })
  //       .then((resp) => resp.json())
  //       .then((data) => {
  //         console.log(data);
  //       })
  //       .catch((error) => {
  //         console.error("Error adding product to cart:", error);
  //       });
  //   }
  // };

  // const addToCart = (productId, selectedSize) => {
  //   if (localStorage.getItem("auth-token")) {
  //     fetch("http://localhost:4002/addtocart", {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json",
  //         "auth-token": localStorage.getItem("auth-token"),
  //       },
  //       body: JSON.stringify({ itemId: productId, itemSize: selectedSize }),
  //     })
  //       .then((resp) => resp.json())
  //       .then((data) => {
  //         console.log(data);
  //       })
  //       .catch((error) => {
  //         console.error("Error adding product to cart:", error);
  //       });
  //   }
  // };

  const addToCart = (productId, selectedSize) => {
    if (localStorage.getItem("auth-token")) {
      axios
        .post(
          "http://localhost:3001/addtocart",
          {
            productId,
            selectedSize,
          },
          {
            headers: {
              "Content-Type": "application/json",
              "auth-token": localStorage.getItem("auth-token"),
            },
          }
        )
        .then((response) => {
          console.log(response.data);
        })
        .catch((error) => {
          console.error("Error adding product to cart:", error);
        });
    }
  };

  //remove from cart
  // const removeFromCart = (itemId) => {
  //   setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  //   if (localStorage.getItem("auth-token")) {
  //     fetch("http://localhost:4000/removefromcart", {
  //       method: "POST",
  //       headers: {
  //         Accept: "application/form-data",
  //         "auth-token": `${localStorage.getItem("auth-token")}`,
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify({ itemId: itemId }),
  //     })
  //       .then((resp) => resp.json())
  //       .then((data) => {
  //         console.log(data);
  //       });
  //   }
  // };
  //remove from cart
  const removeFromCart = (productIdToRemove) => {
    if (localStorage.getItem("auth-token")) {
      axios
        .post(
          "http://localhost:3001/removefromcart",
          {
            productId: productIdToRemove,
          },
          {
            headers: {
              "Content-Type": "application/json",
              "auth-token": localStorage.getItem("auth-token"),
            },
          }
        )
        .then((response) => {
          // Update cartItems state by filtering out the removed item
          setCartItems((prevCartItems) =>
            prevCartItems.filter((item) => item.productId !== productIdToRemove)
          );
          console.log(response.data);
        })
        .catch((error) => {
          console.error("Error removing product from cart:", error);
        });
    }
  };

  //get total cart amount
  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = all_product.find(
          (product) => product.id === Number(item)
        );
        totalAmount += cartItems[item] * itemInfo.new_price;
      }
    }
    return totalAmount;
  };

  //get total cart items
  const getTotalCartItems = () => {
    let totalItem = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        totalItem += cartItems[item];
      }
    }
    return totalItem;
  };

  const contextValue = {
    getTotalCartItems,
    getTotalCartAmount,
    all_product,
    cartItems,
    addToCart,
    removeFromCart,
  };
  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
