import React, { useContext } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } =
    useContext(ShopContext);

  const placeOrder = async () => {
    try {
      const token = getToken();
      const response = await fetch("http://localhost:4000/placeorder", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          cartItems,
          userDetails: {
            name: document.getElementById("name").value,
            phoneNumber: document.getElementById("phoneNumber").value,
            address: document.getElementById("address").value,
            postalCode: document.getElementById("postalCode").value,
          },
        }),
      });

      if (response.ok) {
        console.log("Order placed successfully!");
        // Optionally, you can perform additional actions upon successful order placement
      } else {
        console.error("Failed to place order");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Size</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {all_product.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <div key={e.id}>
              <div className="cartitems-format cartitems-format-main">
                <img src={e.image} alt="" className="carticon-product-icon" />
                <p>{e.name}</p>
                <p className="sizeVal">Size</p>
                <p className="priceNum">₹{e.new_price}</p>
                <button className="cartitems-quantity">
                  {cartItems[e.id]}
                </button>
                <p>₹{e.new_price * cartItems[e.id]}</p>
                <img
                  className="cartitems-remove-icon"
                  src={remove_icon}
                  onClick={() => {
                    removeFromCart(e.id);
                  }}
                  alt=""
                />
              </div>
              <hr />
            </div>
          );
        }
        return null;
      })}
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>₹{getTotalCartAmount()}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>₹{getTotalCartAmount()}</h3>
            </div>
          </div>
          <button onClick={placeOrder}>PLACE ORDER</button>
        </div>
        <div className="cartitems-promocode">
          <p>ENTER YOUR DETAILS</p>
          <div className="cartitems-promobox">
            <input id="name" type="text" placeholder="Name" />
          </div>
          <div className="cartitems-promobox">
            <input id="phoneNumber" type="text" placeholder="Phone Number" />
          </div>
          <div className="cartitems-promobox">
            <input id="address" type="text" placeholder="Address" />
          </div>
          <div className="cartitems-promobox">
            <input id="postalCode" type="text" placeholder="Postal Code" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItems;
