import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);

// //initialising the cart with zeroes
// const getDefaultCart = () => {
//   let cart = {};
//   for (let index = 0; index < 300; index++) {
//     cart[index] = 0;
//   }
//   return cart;
// };

const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);

  //initialising the cart with zeroes
  const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < 300; index++) {
      cart[index] = {
        S: 0,
        M: 0,
        L: 0,
        XL: 0,
        XXL: 0,
      };
    }
    return cart;
  };

  const [cartItems, setCartItems] = useState(getDefaultCart());

  // //fetching data from the database onto the products section
  // useEffect(() => {
  //   fetch("http://localhost:4000/allproducts")
  //     .then((res) => res.json())
  //     .then((data) => setAll_Product(data));

  //   if (localStorage.getItem("auth-token")) {
  //     fetch("http://localhost:4000/getcart", {
  //       method: "POST",
  //       headers: {
  //         Accept: "application/form-data",
  //         "auth-token": `${localStorage.getItem("auth-token")}`,
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify(),
  //     })
  //       .then((resp) => resp.json())
  //       .then((data) => {
  //         setCartItems(data);
  //       });
  //   }
  // }, []);

  useEffect(() => {
    fetch("http://localhost:4000/allproducts")
      .then((res) => res.json())
      .then((data) => {
        console.log("All Products:", data);
        setAll_Product(data);
      });

    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:4000/getcart", {
        method: "POST",
        headers: {
          Accept: "application/form-data",
          "auth-token": `${localStorage.getItem("auth-token")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(),
      })
        .then((resp) => resp.json())
        .then((data) => {
          console.log("Cart Data:", data);
          const productIds = Object.keys(data);
          const updatedCartItems = {};
          productIds.forEach((productId) => {
            const cartItem = data[productId];
            if (cartItem && cartItem.L > 0) {
              const product = all_product.find(
                (item) => item.id === Number(productId)
              );
              updatedCartItems[productId] = {
                ...product,
                quantity: cartItem.L,
              };
            }
          });
          setCartItems(updatedCartItems);
        });
    }
  }, []);

  //add to cart
  const addToCart = (itemId) => {
    setCartItems((prev) => ({
      ...prev,
      [itemId]: {
        ...prev[itemId],
        L: prev[itemId].L + 1,
      },
    }));
    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:4000/addtocart", {
        method: "POST",
        headers: {
          Accept: "application/form-data",
          "auth-token": `${localStorage.getItem("auth-token")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ itemId: itemId }),
      })
        .then((resp) => resp.json())
        .then((data) => {
          console.log(data);
        });
    }
  };

  //remove from cart
  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({
      ...prev,
      [itemId]: {
        ...prev[itemId],
        L: prev[itemId].L - 1,
      },
    }));
    if (localStorage.getItem("auth-token")) {
      fetch("http://localhost:4000/removefromcart", {
        method: "POST",
        headers: {
          Accept: "application/form-data",
          "auth-token": `${localStorage.getItem("auth-token")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ itemId: itemId }),
      })
        .then((resp) => resp.json())
        .then((data) => {
          console.log(data);
        });
    }
  };

  //get total cart amount
  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item].L > 0) {
        let itemInfo = all_product.find(
          (product) => product.id === Number(item)
        );
        totalAmount += cartItems[item].L * itemInfo.new_price;
      }
    }
    return totalAmount;
  };

  //get total cart items
  const getTotalCartItems = () => {
    let totalItem = 0;
    for (const item in cartItems) {
      if (cartItems[item].L > 0) {
        totalItem += cartItems[item].L;
      }
    }
    return totalItem;
  };

  const contextValue = {
    getTotalCartItems,
    getTotalCartAmount,
    all_product,
    cartItems,
    addToCart,
    removeFromCart,
  };
  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
